mapper.insert:
insert into risk_alarm (police_id, alarm_type, 
  alarm_score,is_talk, creation_date, update_date
  )
values (#{policeId,jdbcType=VARCHAR}, #{alarmType,jdbcType=INTEGER}, 
  #{alarmScore,jdbcType=DOUBLE},#{isTalk,jdbcType=INTEGER}, #{creationDate,jdbcType=TIMESTAMP}, #{updateDate,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(*) as num,a.police_id,b.name,b.head_image as headImage, c.name as departmentName,d.position_name as positionName,e.creation_date from risk_alarm a left join user b on b.police_id=a.police_id left join department c on c.id=b.department_id left join police_position d on d.id=b.position_id left join(select police_id,MAX(creation_date) as creation_date from risk_alarm GROUP BY police_id) e on e.police_id=a.police_id where 1=1
and DATE_FORMAT(e.creation_date,'%Y-%m')=#{dateTime}
GROUP BY a.police_id order by count(*) desc limit #{pageNum},#{pageSize}

select count(*) from (select count(*) as num,a.police_id,b.name,b.head_image as headImage, c.name as departmentName,d.position_name as positionName,e.creation_date from risk_alarm a left join user b on b.police_id=a.police_id left join department c on c.id=b.department_id left join police_position d on d.id=b.position_id left join(select police_id,MAX(creation_date) as creation_date from risk_alarm GROUP BY police_id) e on e.police_id=a.police_id where 1=1
and DATE_FORMAT(e.creation_date,'%Y-%m')=#{dateTime}
GROUP BY a.police_id) as t

