mapper.insert:
insert into risk_conduct_bureau_rule_record (id, police_id, type, 
  input_time, content, deduction_score, 
  remarks, creation_date, update_date
  )
values (#{id,jdbcType=INTEGER}, #{policeId,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, 
  #{inputTime,jdbcType=TIMESTAMP}, #{content,jdbcType=VARCHAR}, #{deductionScore,jdbcType=DOUBLE}, 
  #{remarks,jdbcType=VARCHAR}, #{creationDate,jdbcType=TIMESTAMP}, #{updateDate,jdbcType=TIMESTAMP}
  )

mapper.select:
select r1.*, r2.name as type_name from risk_conduct_bureau_rule_record r1, risk_conduct_bureau_rule_type r2
where r1.type = r2.id
and r1.police_id = #{policeId} and DATE_FORMAT(r1.creation_date, '%Y-%m') = #{dateTime}

        and DATE_FORMAT(r1.creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(r1.creation_date, '%Y-%m')<=#{dateTime}
    
    
        and DATE_FORMAT(r1.creation_date, '%Y-%m')=#{dateTime}
    
    ORDER BY r1.creation_date desc
