mapper.select:
select r.*,t.name as type_name, t.name as smallTypeName from risk_conduct_visit_record r
left join risk_conduct_visit_type t on r.type = t.id
where r.id = #{id,jdbcType=INTEGER}

  	SELECT r.*,t.name as type_name FROM risk_conduct_visit_record r,risk_conduct_visit_type t WHERE
	 r.police_id=#{policeId} and r.type=t.id

	    and DATE_FORMAT(r.creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(r.creation_date, '%Y-%m')<=#{dateTime}


	    and DATE_FORMAT(r.creation_date, '%Y-%m')=#{dateTime}

	 ORDER BY r.id desc
