mapper.select:
select a.*,b.name as typeName,a.deduction_score as deductionScore from risk_duty_deal_police_record a
left join risk_case_law_enforcement_type b on b.id=a.type
where a.police_id=#{policeId,jdbcType=VARCHAR}

  and DATE_FORMAT(a.creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(a.creation_date, '%Y-%m')<=#{dateTime}


  and DATE_FORMAT(a.creation_date, '%Y-%m')=#{dateTime}

order by id desc

select record.id, u.police_id as policeId, u.name as policeName, record.police_list_code as policeListCode, record.jurisdiction, itype.name as informationName, etype.name as errorName, record.deduction_score as deductScore, DATE_FORMAT(record.creation_date, '%Y-%m-%d %H:%i:%s') as date from risk_duty_deal_police_record record left join user u on record.police_id = u.police_id left join risk_duty_information_type itype on record.information_id = itype.id left join risk_duty_error_type etype on record.error_id = etype.id where 1 = 1
AND (u.name like CONCAT('%',#{key},'%') OR u.police_id like CONCAT('%',#{key},'%') OR record.content LIKE CONCAT('%',#{key},'%'))
AND itype.id = #{informationId}
AND etype.id = #{errorId}
order by record.id desc limit #{pageIndex}, #{pageSize}

select count(1) from risk_duty_deal_police_record record left join user u on record.police_id = u.police_id left join risk_duty_information_type itype on record.information_id = itype.id left join risk_duty_error_type etype on record.error_id = etype.id where 1 = 1
AND (u.name like CONCAT('%',#{key},'%') OR u.police_id like CONCAT('%',#{key},'%') OR record.content LIKE CONCAT('%',#{key},'%'))
AND itype.id = #{informationId}
AND etype.id = #{errorId}
