mapper.select:
select a.*,b.name as typeName,a.deduction_score as deductionScore from risk_duty_deal_police_record a
left join risk_case_law_enforcement_type b on b.id=a.type
where a.police_id=#{policeId,jdbcType=VARCHAR}

  and DATE_FORMAT(a.creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(a.creation_date, '%Y-%m')<=#{dateTime}


  and DATE_FORMAT(a.creation_date, '%Y-%m')=#{dateTime}

order by id desc

select a.*,b.name as typeName from risk_duty_deal_police_record a left join risk_case_law_enforcement_type b on b.id=a.type LEFT JOIN `user` u ON a.police_id=u.police_id
AND (u.name like CONCAT('%',#{key},'%') OR a.police_id like CONCAT('%',#{key},'%') OR a.content LIKE CONCAT('%',#{key},'%'))
AND a.type=#{type}
order by a.id desc limit #{pageIndex}, #{pageSize}

select count(1) from risk_duty_deal_police_record a left join risk_case_law_enforcement_type b on b.id=a.type LEFT JOIN `user` u ON a.police_id=u.police_id
AND (u.name like CONCAT('%',#{key},'%') OR a.police_id like CONCAT('%',#{key},'%') OR a.content LIKE CONCAT('%',#{key},'%'))
AND a.type=#{type}
