mapper.select:
select pr.id, pr.police_id as policeId, u.name as policeName, pr.deduction_score as deductionScore, prt2.name as typeName, prt1.name as childTypeName, date_format(pr.business_date, '%Y-%m-%d') as businessDate from risk_relevant_record pr left join user u on pr.police_id = u.police_id left join risk_relevant_type prt1 on pr.type_code = prt1.code left join risk_relevant_type prt2 on prt1.p_code = prt2.code where 1 = 1
and prt1.code = #{typeCode}
and (u.police_id like concat('%', #{key}, '%') or u.name like concat('%', #{key}, '%') or prt1.name like concat('%', #{key}, '%') or prt2.name like concat('%', #{key}, '%'))
order by pr.creation_date desc limit #{pageIndex}, #{pageSize}

select count(1) from risk_relevant_record pr left join user u on pr.police_id = u.police_id left join risk_relevant_type prt1 on pr.type_code = prt1.code left join risk_relevant_type prt2 on prt1.p_code = prt2.code where 1 = 1
and prt1.code = #{typeCode}
and (u.police_id like concat('%', #{key}, '%') or u.name like concat('%', #{key}, '%') or prt1.name like concat('%', #{key}, '%') or prt2.name like concat('%', #{key}, '%'))
