mapper.select:
select 

from risk_report_record
where id = #{id,jdbcType=INTEGER}

select a.*,b.name,b.head_image as headImage, c.name as departmentName,d.position_name as positionName, g.total_num as currentTotalNum, round(ifnull((g.total_num-e.total_num)/e.total_num*100,0),2) as riskRate, e.total_num as lastTotalNum,f.alarmTypes from (select police_id,sum(total_num) as total_num,sum(conduct_num) as conduct_num, round(sum(handling_case_num),2) as handling_case_num, sum(duty_num) as duty_num, sum(train_num) as train_num, sum(social_contact_num) as social_contact_num, sum(amily_evaluation_num) as amily_evaluation_num, sum(drink_num) as drink_num, sum(study_num) as study_num,sum(health_num) as health_num,sum(work_num) as work_num from risk_report_record where DATE_FORMAT(creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(creation_date, '%Y-%m')<=#{dateTime} GROUP BY police_id order by ${orderName} desc limit #{num}) a left join user b on b.police_id=a.police_id left join department c on c.id=b.department_id left join police_position d on d.id=b.position_id left join (select * from risk_report_record where DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}) g on g.police_id=a.police_id left join (select * from risk_report_record where DATE_FORMAT(creation_date, '%Y-%m')=#{lastDateTime}) e on e.police_id=a.police_id left join (select GROUP_CONCAT(alarm_type) as alarmTypes,police_id from(SELECT distinct police_id,alarm_type FROM risk_alarm where DATE_FORMAT(creation_date,'%Y-%m')=#{dateTime}) as a GROUP BY police_id) f on f.police_id=a.police_id where 1=1
and (a.police_id like '%${keyWords}%' or b.name like '%${keyWords}%')
order by ${sortName} desc limit #{pageNum},#{pageSize}

  select '行为规范' as name,(case when round(sum(conduct_num),2) >= 25 then 25 else round(sum(conduct_num),2) end) as value from risk_report_record
  where police_id=#{policeId}
    
      and DATE_FORMAT(creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(creation_date, '%Y-%m')<=#{dateTime}
    
    
      and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
    
  UNION ALL
  select '执法办案' as name,(case when round(sum(handling_case_num),2) >= 20 then 20 else round(sum(handling_case_num),2) end) as value from risk_report_record
  where police_id=#{policeId}
    
      and DATE_FORMAT(creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(creation_date, '%Y-%m')<=#{dateTime}
    
    
      and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
    
  UNION ALL
  select '接警执勤' as name,(case when round(sum(duty_num),2) >= 20 then 20 else round(sum(duty_num),2) end) as value from risk_report_record
  where police_id=#{policeId}
    
      and DATE_FORMAT(creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(creation_date, '%Y-%m')<=#{dateTime}
    
    
      and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
    
  UNION ALL
  select '警务技能' as name,(case when round(sum(train_num),2) >= 10 then 10 else round(sum(train_num),2) end) as value from risk_report_record
  where police_id=#{policeId}
    
      and DATE_FORMAT(creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(creation_date, '%Y-%m')<=#{dateTime}
    
    
      and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
    
  UNION ALL
  select '社交' as name,(case when round(sum(social_contact_num),2) >= 15 then 15 else round(sum(social_contact_num),2) end) as value from risk_report_record
  where police_id=#{policeId}
    
      and DATE_FORMAT(creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(creation_date, '%Y-%m')<=#{dateTime}
    
    
      and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
    
  UNION ALL
  select '家属评价' as name,(case when round(sum(amily_evaluation_num),2) >= 5 then 5 else round(sum(amily_evaluation_num),2) end) as value from risk_report_record
  where police_id=#{policeId}
    
      and DATE_FORMAT(creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(creation_date, '%Y-%m')<=#{dateTime}
    
    
      and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
    
UNION ALL
select '健康' as name,(select index_num from risk_health where police_id=#{policeId,jdbcType=VARCHAR}
order by creation_date desc limit 1)

select a.*,b.name,b.head_image as headImage,TIMESTAMPDIFF(YEAR,b.birthday,NOW()) as age, c.name as departmentName,d.position_name as positionName, g.total_num as currentTotalNum, round(ifnull((g.total_num-e.total_num)/e.total_num*100,0),2) as riskRate, e.total_num as lastTotalNum,f.alarmTypes from (select police_id,ifnull(sum(conduct_num), 0) as conduct_num, ifnull(round(sum(handling_case_num),2), 0) as handling_case_num, ifnull(sum(duty_num), 0) as duty_num, ifnull(sum(train_num), 0) as train_num, ifnull(sum(social_contact_num), 0) as social_contact_num, ifnull(sum(amily_evaluation_num), 0) as amily_evaluation_num, sum(drink_num) as drink_num, sum(study_num) as study_num, sum(work_num) as work_num,creation_date from risk_report_record where 1=1 and police_id = #{policeId}
and DATE_FORMAT(creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(creation_date, '%Y-%m')<=#{dateTime}
and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime} ) a left join user b on b.police_id=a.police_id left join department c on c.id=b.department_id left join police_position d on d.id=b.position_id left join (select * from risk_report_record where DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}) g on g.police_id=a.police_id left join (select * from risk_report_record where DATE_FORMAT(creation_date, '%Y-%m')=#{lastDateTime}) e on e.police_id=a.police_id left join (select GROUP_CONCAT(alarm_type) as alarmTypes,police_id from(SELECT distinct police_id,alarm_type FROM risk_alarm where DATE_FORMAT(creation_date,'%Y-%m')=#{dateTime}) as a GROUP BY police_id) f on f.police_id=a.police_id
and a.id = #{id,jdbcType=INTEGER}
and a.police_id = #{policeId} limit 1
