mapper.select:
select a.*,b.name,b.head_image as headImage, c.name as departmentName,d.position_name as positionName, round(ifnull((a.total_num-e.total_num)/e.total_num*100,0),2) as riskRate, e.total_num as lastTotalNum,f.alarmTypes from risk_report_record a left join user b on b.police_id=a.police_id left join department c on c.id=b.department_id left join police_position d on d.id=b.position_id left join (select * from risk_report_record where DATE_FORMAT(creation_date, '%Y-%m')=#{lastDateTime}) e on e.police_id=a.police_id left join (select GROUP_CONCAT(alarm_type) as alarmTypes,police_id from(SELECT distinct police_id,alarm_type FROM risk_alarm where DATE_FORMAT(creation_date,'%Y-%m')=#{dateTime}) as a GROUP BY police_id) f on f.police_id=a.police_id where 1=1 and DATE_FORMAT(a.creation_date, '%Y-%m')=#{dateTime}
and FIND_IN_SET(#{alarmType},f.alarmTypes)>0
and (a.police_id like '%${keyWords}%' or b.name like '%${keyWords}%')
order by ${sortName} desc limit #{pageNum},#{pageSize}

select count(*) from (select a.*,b.name,b.head_image as headImage, c.name as departmentName,d.position_name as positionName, round(ifnull((a.total_num-e.total_num)/e.total_num*100,0),2) as riskRate, e.total_num as lastTotalNum,f.alarmTypes from risk_report_record a left join user b on b.police_id=a.police_id left join department c on c.id=b.department_id left join police_position d on d.id=b.position_id left join (select * from risk_report_record where DATE_FORMAT(creation_date, '%Y-%m')=#{lastDateTime}) e on e.police_id=a.police_id left join (select GROUP_CONCAT(alarm_type) as alarmTypes,police_id from(SELECT distinct police_id,alarm_type FROM risk_alarm where DATE_FORMAT(creation_date,'%Y-%m')=#{dateTime}) as a GROUP BY police_id) f on f.police_id=a.police_id where 1=1 and DATE_FORMAT(a.creation_date, '%Y-%m')=#{dateTime}
and FIND_IN_SET(#{alarmType},f.alarmTypes)>0
and (a.police_id like '%${keyWords}%' or b.name like '%${keyWords}%') ) as t

select '行为规范' as name,conduct_num as value from risk_report_record 
where police_id=#{policeId} and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
UNION ALL
select '执法办案' as name,handling_case_num as value from risk_report_record 
where police_id=#{policeId} and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
UNION ALL
select '接警执勤' as name,duty_num as value from risk_report_record 
where police_id=#{policeId} and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
UNION ALL
select '警务技能' as name,train_num as value from risk_report_record 
where police_id=#{policeId} and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
UNION ALL
select '社交' as name,social_contact_num as value from risk_report_record 
where police_id=#{policeId} and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
UNION ALL
select '家属评价' as name,amily_evaluation_num as value from risk_report_record 
where police_id=#{policeId} and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}
UNION ALL
select '健康' as name,health_num as value from risk_report_record 
where police_id=#{policeId} and DATE_FORMAT(creation_date, '%Y-%m')=#{dateTime}

select a.*,b.name,b.head_image as headImage, c.name as departmentName,d.position_name as positionName, round(ifnull((a.total_num-e.total_num)/e.total_num*100,0),2) as riskRate, e.total_num as lastTotalNum from risk_report_record a left join user b on b.police_id=a.police_id left join department c on c.id=b.department_id left join police_position d on d.id=b.position_id left join (select * from risk_report_record where DATE_FORMAT(creation_date, '%Y-%m')=#{lastDateTime}) e on e.police_id=a.police_id where 1=1
and a.id = #{id,jdbcType=INTEGER}
and a.police_id = #{policeId} and DATE_FORMAT(a.creation_date, '%Y-%m')=#{dateTime}
