mapper.select:
select 

from train_firearm_achievement
where id = #{id,jdbcType=INTEGER}

select a.*,b.name as projectName,c.unit_name as unitName,d.head_image as headImage,d.name, e.name as trainFirearmName,e.train_end_date as trainEndDate,e.train_start_date as trainStartDate from train_firearm_achievement a left join train_project b on b.id=a.train_project_type left join train_unit c on c.id=b.unit_id left join user d on d.police_id=a.police_id left join train_firearm e on e.id=a.train_firearm_id where 1=1
and a.id = #{id,jdbcType=INTEGER}
and a.train_firearm_id=#{trainFirearmId,jdbcType=INTEGER}
and a.police_id = #{policeId,jdbcType=VARCHAR}

select a.*,b.name as police_name,c.name as department_name,e.unit_name from train_firearm_achievement a left join user b on a.police_id = b.police_id left join department c on b.department_id = c.id left join train_project d on a.train_project_type = d.id left join train_unit e on d.unit_id = e.id where train_firearm_id = #{trainFirearmId}
and c.id = #{departmentId}
and (a.police_id like "%"#{keyword}"%" or b.name like "%"#{keyword}"%")
limit #{pageSize},10

select count(*) as count from train_firearm_achievement a 
left join user b on a.police_id = b.police_id 
left join department c on b.department_id = c.id
where train_firearm_id = #{trainFirearmId} 

and c.id = #{departmentId}


and (a.police_id like "%"#{keyword}"%" or b.name like "%"#{keyword}"%")

select IFNULL(rank,0) from (select *,(case when @p=achievement then @r
when @p:=achievement then @r:=@r+1
end) as rank from (select * from train_firearm_achievement 
where train_firearm_id = #{trainFirearmId} 
order by achievement  

desc 

) a, (select @r:=0,@p:=NULL) r ) a 
where a.police_id = #{policeId}

select count(distinct(police_id)) as count from train_firearm_achievement 
where train_firearm_id = #{trainFirearmId} 
and is_sign = 2

select count(distinct(b.department_id)) as count from train_firearm_achievement a 
left join user b on a.police_id = b.police_id 
where a.train_firearm_id = #{trainFirearmId}

select b.id,c.name as projectName,a.achievement_grade, a.achievement_str as achievementStr, (case when a.achievement_grade=2 then '合格' when a.achievement_grade=3 then '良好' when a.achievement_grade=4 then '优秀' else '不合格' end) as achievementGradeStr ,a.creation_date from train_firearm_achievement a left join train_firearm b on b.id=a.train_firearm_id left join train_project c on c.id=a.train_project_type where a.police_id=#{policeId}
and DATE_FORMAT(a.creation_date, '%Y-%m')>=#{lastMonthTime} and DATE_FORMAT(a.creation_date, '%Y-%m')<=#{dateTime}
and DATE_FORMAT(a.creation_date, '%Y-%m')=#{dateTime}
order by a.creation_date desc

select u.name as police_name, d.name as department_name, fa.* from train_firearm_achievement fa left join user u on fa.police_id = u.police_id left join department d on d.id = u.department_id where train_firearm_id = #{firearmId}
and (u.police_id like concat('%', #{key}, '%') or u.name like concat('%', #{key}, '%'))
and fa.achievement_grade = 1
and fa.achievement_grade = 2
and u.department_id = #{deptId}
order by fa.id desc limit #{pageIndex}, #{pageSize}

select count(1) from train_firearm_achievement fa left join user u on fa.police_id = u.police_id left join department d on d.id = u.department_id where train_firearm_id = #{firearmId}
and (u.police_id like concat('%', #{key}, '%') or u.name like concat('%', #{key}, '%'))
and fa.achievement_grade = 1
and fa.achievement_grade = 2
and u.department_id = #{deptId}

select * from train_firearm_achievement a where 1 = 1

    and DATE_FORMAT(sign_date,'%Y') = #{firearmRecordYear}


    and DATE_FORMAT(sign_date,'%m') = #{firearmRecordMonth}


    and a.police_id = #{policeId}

order by creation_date desc

SELECT
    CEIL(AVG(achievement))
FROM train_firearm_achievement
WHERE police_id = #{policeId}
  and DATE_FORMAT(sign_date,'%Y-%m') >= #{beginDate}
  and DATE_FORMAT(sign_date,'%Y-%m') <= #{endDate}
