mapper.select:
    select a.*,b.name as train_firearm_type_name, c.name as department_name from train_firearm a
left join train_project b on a.train_firearm_type = b.id
left join department c on a.department_id = c.id
where 1 = 1
	
	 and a.department_id = #{departmentId}
	
	
	 and a.status = #{status}
	
    
		and DATE_FORMAT(a.registration_start_date,'%Y-%m-%d')<=#{reprotTime} and DATE_FORMAT(a.registration_end_date,'%Y-%m-%d')>=#{reprotTime}
	
	
		and DATE_FORMAT(a.train_start_date,'%Y-%m-%d')<=#{signTime} and DATE_FORMAT(a.train_end_date,'%Y-%m-%d')>=#{signTime}
	
    
      and (a.name = #{keyWords} or a.place = #{keyWords})
    
	order by id desc
	
	  limit #{pageSize},10

    select count(*) from train_firearm a
    left join train_project b on a.train_firearm_type = b.id
    where 1 = 1
    
	 and department_id = #{departmentId}
	
   
	 and a.status = #{status}
	
    
		and DATE_FORMAT(a.registration_start_date,'%Y-%m-%d')<=#{reprotTime} and DATE_FORMAT(a.registration_end_date,'%Y-%m-%d')>=#{reprotTime}
	
	
		and DATE_FORMAT(a.train_start_date,'%Y-%m-%d')<=#{signTime} and DATE_FORMAT(a.train_end_date,'%Y-%m-%d')>=#{signTime}
	
    
      and (a.name = #{keyWords} or a.place = #{keyWords})
