and department_id = #{departmentId}

and police_id != '000001'

select b.* from train_physical_achievement_details a
left join user b on b.police_id=a.police_id
where a.project_id=#{projectId} and a.train_physical_id=#{trainPhysicalId}

select count(*) from user
where 1=1 and department_id!=47
     
        and department_id = #{departmentId}
      
      
        and position_id = #{positionId}
      
      
        and is_unit_leader = #{isUnitLeader}
      
      
        and is_cadre = #{isCadre}

select name,police_id from user where 1 = 1

    and department_id = #{deptId}

and police_id != '000001'

SELECT u.head_image headImage, u.police_id policeId, u.name `name`, c.position_name policeType, k.police_king_name kingName, d.`name` policeDeptName, YEAR (NOW()) - SUBSTR(u.id_card, 7, 4) age, epi.`score` FROM (SELECT police_id, SUM(score) AS score FROM excellent_police_info where 1 = 1
and label = #{labelId}
GROUP BY police_id) epi LEFT JOIN `user` u ON epi.`police_id` = u.`police_id` LEFT JOIN department d ON u.department_id = d.id LEFT JOIN police_kind k ON u.kind_id = k.id LEFT JOIN police_position c ON c.id = u.position_id LEFT JOIN excellent_police_info l ON u.police_id=l.police_id LEFT JOIN police_label p ON l.label=p.id WHERE 1 = 1
and u.gender = #{gender}
AND u.degree = #{degree}
and YEAR (now()) - SUBSTR( u.id_card, 7, 4 ) BETWEEN #{minAge} and YEAR (now()) - SUBSTR( u.id_card, 7, 4 )
and YEAR (now()) - SUBSTR( u.id_card, 7, 4 ) BETWEEN YEAR(now()) - SUBSTR( u.id_card, 7, 4 ) and #{maxAge}
and YEAR (now()) - SUBSTR( u.id_card, 7, 4 ) BETWEEN #{minAge} and #{maxAge}
and u.political_status like concat('%', #{politicalStatus}, '%')
and u.native_place like concat('%', #{nativePlace}, '%')
and u.marriage_status = #{marriageStatus}
and k.id = #{kindId}
and year(NOW()) - year(u.working_start_date) BETWEEN 1 and 3
and year(NOW()) - year(u.working_start_date) BETWEEN 3 and 5
and year(NOW()) - year(u.working_start_date) BETWEEN 5 and 10
and year(NOW()) - year(u.working_start_date) >= 10
and year(NOW()) - year(u.employment_date) BETWEEN 1 and 3
and year(NOW()) - year(u.employment_date) BETWEEN 3 and 5
and year(NOW()) - year(u.employment_date) BETWEEN 5 and 10
and year(NOW()) - year(u.employment_date) >= 10
and d.id = #{deptId}
and (u.police_id rlike #{likeName} or u.name rlike #{likeName} or p.label_name rlike #{likeName})
group by u.police_id ORDER BY epi.`score` DESC limit #{pageIndex}, #{pageSize}

SELECT count(1) FROM (SELECT police_id, SUM(score) AS score FROM excellent_police_info where 1 = 1
and label = #{labelId}
GROUP BY police_id) epi LEFT JOIN `user` u ON epi.`police_id` = u.`police_id` LEFT JOIN department d ON u.department_id = d.id LEFT JOIN police_kind k ON u.kind_id = k.id LEFT JOIN police_position c ON c.id = u.position_id WHERE 1 = 1
and u.gender = #{gender}
AND u.degree = #{degree}
and YEAR (now()) - SUBSTR( u.id_card, 7, 4 ) BETWEEN #{minAge} and YEAR (now()) - SUBSTR( u.id_card, 7, 4 )
and YEAR (now()) - SUBSTR( u.id_card, 7, 4 ) BETWEEN YEAR(now()) - SUBSTR( u.id_card, 7, 4 ) and #{maxAge}
and YEAR (now()) - SUBSTR( u.id_card, 7, 4 ) BETWEEN #{minAge} and #{maxAge}
and u.political_status like concat('%', #{politicalStatus}, '%')
and u.native_place like concat('%', #{nativePlace}, '%')
and u.marriage_status = #{marriageStatus}
and k.id = #{kindId}
and year(NOW()) - year(u.working_start_date) BETWEEN 1 and 3
and year(NOW()) - year(u.working_start_date) BETWEEN 3 and 5
and year(NOW()) - year(u.working_start_date) BETWEEN 5 and 10
and year(NOW()) - year(u.working_start_date) >= 10
and year(NOW()) - year(u.employment_date) BETWEEN 1 and 3
and year(NOW()) - year(u.employment_date) BETWEEN 3 and 5
and year(NOW()) - year(u.employment_date) BETWEEN 5 and 10
and year(NOW()) - year(u.employment_date) >= 10
and d.id = #{deptId}
and (u.police_id rlike #{likeName} or u.name rlike #{likeName} or p.label_name rlike #{likeName})
